package com.aviator.ketty.controller;import com.aviator.ketty.core.JsonResult;import com.aviator.ketty.service.TestService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.util.Map;/** * @Author ketty bluce * @Create 2023/6/15 * @Version 1.0 */@Api(tags = "脚本验算")@RestController@RequestMapping("/api/v1/param")public class ParamTestController {    @Resource    private TestService testService;    @ApiOperation(value = "计算脚本")    @PostMapping("count")    public JsonResult<Object> getCountTest(@RequestBody Map<String, Object> paramMap) {        JsonResult paramTest = testService.getParamTest(paramMap);        return JsonResult.buildSuccess(paramTest);    }}