package com.aviator.ketty.test;import java.util.ArrayList;import java.util.Comparator;import java.util.List;/** * @Author ketty bluce * @Create 2023/5/25 * @Version 1.0 */public class test {    public static void main(String[] args) {        List<User> li = new ArrayList<>();        User u1 = new User();        u1.setName("1");        u1.setScore("张三");        User u2 = new User();        u2.setName("10");        u2.setScore("李四");        User u3 = new User();        u3.setName("5");        u3.setScore("王蓉");        li.add(u1);        li.add(u2);        li.add(u3);        li.sort(Comparator.comparing((User u) -> u.getName()).reversed());        li.stream().forEach(user -> System.out.println(user));    }}