package com.aviator.ketty.verify;import java.util.regex.Matcher;import java.util.regex.Pattern;/** * @Author ketty bluce * @Create 2023/6/30 * @Version 1.0 */public class test {    public static void main(String[] args) {        String string = "##脚本：ZA 与 HA 与 AA_1 同时存在";        Pattern pattern = Pattern.compile("脚本：(.*?)与(.*?)与(.*?)同时存在");        Matcher matcher = pattern.matcher(string);        if (matcher.find()) {            String result = matcher.group(1);            System.out.println(result);            result = matcher.group(2);            System.out.println(result);            result = matcher.group(3);            System.out.println(result);        }    }}